    private void hitungBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungBtnActionPerformed
        try {
        // Ambil nilai harga asli
        double hargaAsli = Double.parseDouble(hargaAsliTxt.getText());
        
        // Ambil persentase diskon dari JComboBox
        String diskonStr = (String) diskonCmb.getSelectedItem();
        int diskonPersen = Integer.parseInt(diskonStr.replace("%", ""));
        
        // Hitung jumlah diskon
        double jumlahDiskon = (hargaAsli * diskonPersen) / 100;
        
        // Hitung harga setelah diskon
        double hargaAkhir = hargaAsli - jumlahDiskon;
        
        // Tampilkan hasil di JTextField dan JLabel
        hargaSetelahDiskonTxt.setText(String.format("%.2f", hargaAkhir));
        hematLbl.setText("Anda menghemat: Rp " + String.format("%.2f", jumlahDiskon));
        
         // Simpan riwayat perhitungan
        String riwayat = "Harga Asli: Rp " + hargaAsliTxt.getText() +
                         ", Diskon: " + diskonCmb.getSelectedItem() +
                         ", Harga Setelah Diskon: Rp " + hargaSetelahDiskonTxt.getText() + "\n";

           // Pastikan riwayat tidak kosong sebelum menambahkan
        if (riwayatTxtArea.getText().isEmpty()) {
            riwayatTxtArea.setText(riwayat);
        } else {
            riwayatTxtArea.append(riwayat);
        }
        
    } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, "Masukkan harga yang valid!", "Error", JOptionPane.ERROR_MESSAGE);
    }
        
    }//GEN-LAST:event_hitungBtnActionPerformed

