    private void hargaAsliTxtKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_hargaAsliTxtKeyTyped
        char karakter = evt.getKeyChar();
        if (!Character.isDigit(karakter) && karakter != '.') {
            evt.consume(); // Hanya angka dan titik yang bisa dimasukkan
        }
    }//GEN-LAST:event_hargaAsliTxtKeyTyped

